// src/Downloads.js
import React, { useState, useEffect, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './App.css';
import appLogo from './assets/summarease.png';
import companyLogo from './assets/creme_brulee.jpg';
import { Link } from 'react-router-dom';
import SearchBar from './SearchBar';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import rehypeRaw from 'rehype-raw';
import rehypeHighlight from 'rehype-highlight';
import { summaryContent } from './summaryContent';
import { qnaContent } from './qnaContent';
import { quizContent } from './quizContent';


function Downloads() {
  const location = useLocation();
  const navigate = useNavigate();
  const [selectedMode, setSelectedMode] = useState(null);
  const [processing, setProcessing] = useState(false);
  const [result, setResult] = useState(null);
  const [markdownContent, setMarkdownContent] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');
  const [showSuccess, setShowSuccess] = useState(false);
  const fileInputRef = useRef(null);

  // Get files from state if available
  const files = location.state?.files || [];

  // Handle file selection
  const handleFileSelect = (e) => {
    const selectedFiles = Array.from(e.target.files);
    navigate('/downloads', { state: { files: selectedFiles } });
  };

  const handleClick = () => {
    fileInputRef.current.click();
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFiles = Array.from(e.dataTransfer.files);
    navigate('/downloads', { state: { files: droppedFiles } });
  };

  const handleProcess = (mode) => {
    setSelectedMode(mode);

    // For summary mode, display hardcoded summary content
    if (mode === 1) {
      setMarkdownContent(summaryContent);
    } else if (mode === 2) {
      setMarkdownContent(qnaContent);
    } else {
      // For quiz mode, display hardcoded quiz content
      setMarkdownContent(quizContent);
    }
  };

  const handleSubmit = () => {
    if (files.length === 0) {
      alert('Please upload files first');
      return;
    }

    setSuccessMessage('Files submitted successfully!');
    setShowSuccess(true);

    // Redirect to Downloads after a short delay
    setTimeout(() => {
      navigate('/downloads');
    }, 2000);
  };

  return (
    <div className="App">
      <div className="background-logo" style={{ backgroundImage: `url(${companyLogo})` }} />
      <header className="app-header">
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img src={appLogo} alt="App Logo" className="header-logo" />
          <SearchBar />
        </div>
        <nav className="nav-menu">
          <Link to="/" className="nav-item">Home</Link>
          <Link to="/downloads" className="nav-item">Downloads</Link>
          <Link to="/history" className="nav-item">User History</Link>
        </nav>
      </header>

      <main className="content">
        <h1>Here are some things you can download to help:</h1>

        {showSuccess && (
          <div className="success-message">
            {successMessage}
          </div>
        )}

        {processing ? (
          <div className="processing">
            <h2>Processing files...</h2>
            <p>Please wait while we process your files.</p>
          </div>
        ) : selectedMode ? (
          <div className="result">
            <h2>Results:</h2>
            {selectedMode && markdownContent && (
              <div className="markdown-container">
                <ReactMarkdown
                  remarkPlugins={[remarkGfm]}
                  rehypePlugins={[rehypeRaw, rehypeHighlight]}
                  components={{
                    p: ({ children }) => <p className="markdown-content">{children}</p>,
                    h1: ({ children }) => <h1 className="markdown-content">{children}</h1>,
                    h2: ({ children }) => <h2 className="markdown-content">{children}</h2>,
                    h3: ({ children }) => <h3 className="markdown-content">{children}</h3>,
                    ul: ({ children }) => <ul className="markdown-content">{children}</ul>,
                    ol: ({ children }) => <ol className="markdown-content">{children}</ol>,
                    li: ({ children }) => <li className="markdown-content">{children}</li>
                  }}
                >
                  {markdownContent}
                </ReactMarkdown>
              </div>
            )}
            <button 
              className="btn eraser pink" 
              onClick={() => setSelectedMode(null)}
            >
              Back to Menu
            </button>
          </div>
        ) : (
          <div className="buttons">
            <button 
              className="btn eraser pink" 
              onClick={() => handleProcess(1)}
              disabled={processing}
            >
              Get Summary
            </button>
            <button 
              className="btn eraser green" 
              onClick={() => handleProcess(2)}
              disabled={processing}
            >
              Get Q&A
            </button>
            <button 
              className="btn eraser blue" 
              onClick={() => handleProcess(3)}
              disabled={processing}
            >
              Get Quiz
            </button>
          </div>
        )}
        <div className="drop-zone" onDragOver={handleDragOver} onDrop={handleDrop}>
          <h2>Drag and drop files here</h2>
          <p>or click to select files</p>
          <input type="file" multiple onChange={handleFileSelect} style={{ display: 'none' }} ref={fileInputRef} />
          <button onClick={handleClick} className="submit-button">Select Files</button>
        </div>

        {files.length > 0 && (
          <div className="selected-files">
            <h3>Selected Files:</h3>
            <ul>
              {files.map((file, index) => (
                <li key={index}>{file.name}</li>
              ))}
            </ul>
            <button onClick={handleSubmit} className="submit-button">Submit Files</button>
          </div>
        )}
      </main>
    </div>
  );
}

export default Downloads;
