// src/Home.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';
import appLogo from './assets/summarease.png';
import companyLogo from './assets/creme_brulee.jpg';
import { Link } from 'react-router-dom';
import SearchBar from './SearchBar';


function Home() {
  const navigate = useNavigate();
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [dragOver, setDragOver] = useState(false);
  const [processing, setProcessing] = useState(false);

  const handleFileSelect = (event) => {
    const files = Array.from(event.target.files);
    setSelectedFiles(prev => [...prev, ...files]);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = () => {
    setDragOver(false);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setDragOver(false);
    const files = Array.from(event.dataTransfer.files);
    setSelectedFiles(prev => [...prev, ...files]);
  };

  const handlePDFClick = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.pdf';
    input.multiple = true;
    input.onchange = handleFileSelect;
    input.click();
  };

  const handleVideoClick = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'video/*';
    input.multiple = true;
    input.onchange = handleFileSelect;
    input.click();
  };

  const handleSubmit = async () => {
    if (selectedFiles.length === 0) return;

    setProcessing(true);
    try {
      // Create an array to store processed files
      const processedFiles = [];

      // Process each file
      for (const file of selectedFiles) {
        const fileType = file.name.split('.').pop().toLowerCase();

        // Process PDF files
        if (fileType === 'pdf') {
          const formData = new FormData();
          formData.append('file', file);

          const response = await fetch('http://localhost:5000/process-pdf', {
            method: 'POST',
            body: formData
          });

          const result = await response.json();

          if (result.error) {
            throw new Error(result.error);
          }

          processedFiles.push({
            originalName: file.name,
            type: fileType,
            processed: true
          });
        } else {
          processedFiles.push({
            originalName: file.name,
            type: fileType,
            processed: false
          });
        }
      }

      // Redirect to downloads page with processed files
      navigate('/downloads', { 
        state: { 
          files: processedFiles,
          processingComplete: true 
        } 
      });

      // Reset after processing
      setSelectedFiles([]);
      setProcessing(false);
    } catch (error) {
      console.error('Error processing files:', error);
      setProcessing(false);
      alert('Error processing files. Please try again.');
    }
  };

  // Helper functions for file processing
// All PDF processing is now handled by the Python server
  // This function is no longer needed since we're using the Python server endpoint

  return (
    <div className="App">
      <div className="background-logo" style={{ backgroundImage: `url(${companyLogo})` }} />

		<header className="app-header">
		  <div style={{ display: 'flex', alignItems: 'center' }}>
			<img src={appLogo} alt="App Logo" className="header-logo" />
			<SearchBar />
		  </div>
		  <nav className="nav-menu">
			<Link to="/" className="nav-item">Home</Link>
			<Link to="/downloads" className="nav-item">Downloads</Link>
			<Link to="/history" className="nav-item">User History</Link>
		  </nav>
		</header>

      <main className="content">
        <h1>Welcome to Summarease</h1>
        <div 
          className={`drop-zone ${dragOver ? 'drag-over' : ''}`} 
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
        >
          <div className="buttons">
            <button 
              className="btn eraser pink" 
              onClick={handlePDFClick}
            >
              📝 Add PDF Files
            </button>
            <button 
              className="btn eraser blue" 
              onClick={handleVideoClick}
            >
              🎥 Add Videos
            </button>
          </div>
          {selectedFiles.length > 0 && (
            <div className="selected-files">
              <h3>Selected Files:</h3>
              <ul>
                {selectedFiles.map((file, index) => (
                  <li key={index}>{file.name}</li>
                ))}
              </ul>
              <button 
                className="submit-button" 
                onClick={handleSubmit}
                disabled={processing}
              >
                {processing ? 'Processing...' : '🚀 Submit Files'}
              </button>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default Home;
